mapper.update:
UPDATE bbs SET subject = #{subject}, content = #{content},
	saveFilename = #{saveFilename, jdbcType=VARCHAR},
	originalFilename = #{originalFilename, jdbcType=VARCHAR}
WHERE num = #{num}

mapper.select:
SELECT COUNT(*)
FROM bbsLike
WHERE num = #{num}

SELECT num, empId
FROM bbsLike
WHERE num = #{num} AND empId = #{empId}
