mapper.select:
SELECT NVL(COUNT(*), 0) FROM reports r JOIN employee2 e2 ON r.reporter_id = e2.empId
report_status = #{status}
AND

SELECT NVL(COUNT(*), 0) FROM ( SELECT target, target_num, content_type, content_title FROM reports
report_status = #{status}
GROUP BY target, target_num, content_type, content_title )

SELECT target, target_num, content_type, content_title, NVL(COUNT(*), 0) reportsCount FROM reports
report_status = #{status}
GROUP BY target, target_num, content_type, content_title ORDER BY reportsCount DESC OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY

SELECT NVL(COUNT(*), 0) FROM reports r
num != #{num} AND
target = #{target} AND target_num = #{target_num}

mapper.update:
UPDATE reports SET report_status = #{report_status}, action_taken = #{action_taken},
	processor_id = #{processor_id}, processed_date = SYSDATE



			target = #{target} AND target_num = #{target_num}


			num = #{num}
